"use client"

import { useState, useEffect } from "react"
import { Button } from "../../../components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "../../../components/ui/card"
import { Badge } from "../../../components/ui/badge"
import { Input } from "../../../components/ui/input"
import { Label } from "../../../components/ui/label"
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "../../../components/ui/dialog"
import { ScrollArea } from "../../../components/ui/scroll-area"
import { Search, ImageIcon, Plus, X, Save, RefreshCw } from "lucide-react"
import productsData from "../../../data/products.json"

interface BlobFile {
  url: string
  pathname: string
  filename: string
  size: number
  sizeFormatted: string
  contentType: string
  isImage: boolean
  uploadedAt: string
}

interface ProductMapping {
  slug: string
  title: string
  type: string
  currentImage: string
  blobImages: string[]
  primaryBlobImage?: string
}

export default function ProductMappingPage() {
  const [blobFiles, setBlobFiles] = useState<BlobFile[]>([])
  const [productMappings, setProductMappings] = useState<ProductMapping[]>([])
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedType, setSelectedType] = useState<string>("all")
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)

  // Initialize product mappings from JSON data
  useEffect(() => {
    const mappings: ProductMapping[] = productsData.items.map((product) => ({
      slug: product.slug,
      title: product.title,
      type: product.type,
      currentImage: product.image,
      blobImages: [],
      primaryBlobImage: undefined,
    }))
    setProductMappings(mappings)
  }, [])

  // Fetch blob files
  const fetchBlobFiles = async () => {
    try {
      const response = await fetch("/api/list")
      if (!response.ok) throw new Error("Failed to fetch blob files")
      const data = await response.json()
      setBlobFiles(data.files.filter((f: BlobFile) => f.isImage))
    } catch (error) {
      console.error("Error fetching blob files:", error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchBlobFiles()
  }, [])

  // Filter products
  const filteredProducts = productMappings.filter((product) => {
    const matchesSearch = product.title.toLowerCase().includes(searchTerm.toLowerCase())
    const matchesType = selectedType === "all" || product.type === selectedType
    return matchesSearch && matchesType
  })

  // Get unique product types
  const productTypes = Array.from(new Set(productsData.items.map((p) => p.type)))

  // Add blob image to product
  const addBlobImage = (productSlug: string, blobUrl: string) => {
    setProductMappings((prev) =>
      prev.map((product) =>
        product.slug === productSlug
          ? {
              ...product,
              blobImages: [...product.blobImages, blobUrl],
              primaryBlobImage: product.primaryBlobImage || blobUrl,
            }
          : product,
      ),
    )
  }

  // Remove blob image from product
  const removeBlobImage = (productSlug: string, blobUrl: string) => {
    setProductMappings((prev) =>
      prev.map((product) =>
        product.slug === productSlug
          ? {
              ...product,
              blobImages: product.blobImages.filter((url) => url !== blobUrl),
              primaryBlobImage: product.primaryBlobImage === blobUrl ? product.blobImages[0] : product.primaryBlobImage,
            }
          : product,
      ),
    )
  }

  // Set primary image
  const setPrimaryImage = (productSlug: string, blobUrl: string) => {
    setProductMappings((prev) =>
      prev.map((product) => (product.slug === productSlug ? { ...product, primaryBlobImage: blobUrl } : product)),
    )
  }

  // Save mappings (this would typically save to a database)
  const saveMappings = async () => {
    setSaving(true)
    try {
      await new Promise((resolve) => setTimeout(resolve, 1000))
      console.log("Product mappings saved:", productMappings)
      alert("Product mappings saved successfully!")
    } catch (error) {
      console.error("Error saving mappings:", error)
      alert("Error saving mappings")
    } finally {
      setSaving(false)
    }
  }

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center min-h-[400px]">
          <RefreshCw className="h-8 w-8 animate-spin" />
        </div>
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">Product Image Mapping</h1>
        <Button onClick={saveMappings} disabled={saving}>
          <Save className="h-4 w-4 mr-2" />
          {saving ? "Saving..." : "Save Mappings"}
        </Button>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Total Products</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{productMappings.length}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Mapped Products</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">
              {productMappings.filter((p) => p.blobImages.length > 0).length}
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Available Images</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-blue-600">{blobFiles.length}</div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Unmapped Products</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-orange-600">
              {productMappings.filter((p) => p.blobImages.length === 0).length}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        <div className="flex-1">
          <Label htmlFor="search">Search Products</Label>
          <div className="relative">
            <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
            <Input
              id="search"
              placeholder="Search by product name..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
        </div>
        <div>
          <Label htmlFor="type">Filter by Type</Label>
          <select
            id="type"
            value={selectedType}
            onChange={(e) => setSelectedType(e.target.value)}
            className="w-full p-2 border rounded-md"
          >
            <option value="all">All Types</option>
            {productTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-6">
        {filteredProducts.map((product) => (
          <Card key={product.slug} className="overflow-hidden">
            <CardHeader className="pb-3">
              <div className="flex items-start justify-between">
                <div>
                  <CardTitle className="text-lg">{product.title}</CardTitle>
                  <Badge variant="outline" className="mt-1">
                    {product.type}
                  </Badge>
                </div>
                <Dialog>
                  <DialogTrigger asChild>
                    <Button size="sm" variant="outline">
                      <Plus className="h-4 w-4 mr-1" />
                      Add Images
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-4xl max-h-[80vh]">
                    <DialogHeader>
                      <DialogTitle>Add Images to {product.title}</DialogTitle>
                    </DialogHeader>
                    <ScrollArea className="h-[60vh]">
                      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 p-4">
                        {blobFiles.map((file) => (
                          <div key={file.url} className="space-y-2">
                            <div className="aspect-square bg-gray-100 rounded-md overflow-hidden">
                              <img
                                src={file.url || "/placeholder.svg"}
                                alt={file.filename}
                                className="w-full h-full object-cover cursor-pointer hover:opacity-80"
                                onClick={() => addBlobImage(product.slug, file.url)}
                              />
                            </div>
                            <p className="text-xs text-center truncate" title={file.filename}>
                              {file.filename}
                            </p>
                            <Button
                              size="sm"
                              className="w-full"
                              onClick={() => addBlobImage(product.slug, file.url)}
                              disabled={product.blobImages.includes(file.url)}
                            >
                              {product.blobImages.includes(file.url) ? "Added" : "Add"}
                            </Button>
                          </div>
                        ))}
                      </div>
                    </ScrollArea>
                  </DialogContent>
                </Dialog>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Current Static Image */}
              <div>
                <Label className="text-sm font-medium">Current Static Image</Label>
                <div className="mt-2 aspect-video bg-gray-100 rounded-md overflow-hidden">
                  <img
                    src={product.currentImage || "/placeholder.svg"}
                    alt={product.title}
                    className="w-full h-full object-cover"
                  />
                </div>
              </div>

              {/* Blob Images */}
              {product.blobImages.length > 0 && (
                <div>
                  <Label className="text-sm font-medium">
                    Blob Images ({product.blobImages.length})
                    {product.primaryBlobImage && (
                      <Badge variant="secondary" className="ml-2 text-xs">
                        Primary Set
                      </Badge>
                    )}
                  </Label>
                  <div className="mt-2 grid grid-cols-2 gap-2">
                    {product.blobImages.map((imageUrl, index) => (
                      <div key={imageUrl} className="relative group">
                        <div
                          className={`aspect-video bg-gray-100 rounded-md overflow-hidden border-2 ${
                            product.primaryBlobImage === imageUrl ? "border-blue-500" : "border-transparent"
                          }`}
                        >
                          <img
                            src={imageUrl || "/placeholder.svg"}
                            alt={`${product.title} ${index + 1}`}
                            className="w-full h-full object-cover"
                          />
                        </div>
                        <div className="absolute top-1 right-1 flex gap-1">
                          <Button
                            size="sm"
                            variant="secondary"
                            className="h-6 w-6 p-0 opacity-0 group-hover:opacity-100"
                            onClick={() => setPrimaryImage(product.slug, imageUrl)}
                            title="Set as primary"
                          >
                            <ImageIcon className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="destructive"
                            className="h-6 w-6 p-0 opacity-0 group-hover:opacity-100"
                            onClick={() => removeBlobImage(product.slug, imageUrl)}
                          >
                            <X className="h-3 w-3" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {product.blobImages.length === 0 && (
                <div className="text-center py-4 text-gray-500">
                  <ImageIcon className="h-8 w-8 mx-auto mb-2 opacity-50" />
                  <p className="text-sm">No blob images assigned</p>
                </div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Button } from "../../../components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "../../../components/ui/card"
import { Input } from "../../../components/ui/input"
import { Label } from "../../../components/ui/label"
import { Badge } from "../../../components/ui/badge"
import { MediaSelector } from "../../../components/MediaSelector"
import { useToast } from "../../../hooks/use-toast"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../../../components/ui/tabs"
import { Textarea } from "../../../components/ui/textarea"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../../../components/ui/select"

interface Product {
  slug: string
  title: string
  type: string
  priceMin: number
  priceMax?: number
  image: string
  badges?: string[]
  description?: string
  gallery?: string[]
}

interface BlobFile {
  url: string
  pathname: string
  size: number
  uploadedAt: string
  filename: string
}

export default function ProductManagement() {
  const [products, setProducts] = useState<Product[]>([])
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null)
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const { toast } = useToast()

  useEffect(() => {
    loadProducts()
  }, [])

  const loadProducts = async () => {
    try {
      const response = await fetch("/data/products.json")
      const data = await response.json()
      setProducts(data.items || [])
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load products",
      })
    } finally {
      setLoading(false)
    }
  }

  const handleImageSelect = (files: BlobFile[]) => {
    if (selectedProduct && files.length > 0) {
      const updatedProduct = {
        ...selectedProduct,
        image: files[0].url,
      }
      setSelectedProduct(updatedProduct)
      updateProductInList(updatedProduct)
    }
  }

  const handleGallerySelect = (files: BlobFile[]) => {
    if (selectedProduct) {
      const updatedProduct = {
        ...selectedProduct,
        gallery: files.map((f) => f.url),
      }
      setSelectedProduct(updatedProduct)
      updateProductInList(updatedProduct)
    }
  }

  const updateProductInList = (updatedProduct: Product) => {
    setProducts(products.map((p) => (p.slug === updatedProduct.slug ? updatedProduct : p)))
  }

  const handleProductUpdate = (field: keyof Product, value: any) => {
    if (selectedProduct) {
      const updatedProduct = {
        ...selectedProduct,
        [field]: value,
      }
      setSelectedProduct(updatedProduct)
      updateProductInList(updatedProduct)
    }
  }

  const saveProducts = async () => {
    setSaving(true)
    try {
      // In a real app, this would save to a database
      // For now, we'll just show success
      toast({
        title: "Success",
        description: "Products updated successfully",
      })
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to save products",
      })
    } finally {
      setSaving(false)
    }
  }

  if (loading) {
    return <div className="p-8">Loading products...</div>
  }

  return (
    <div className="container mx-auto p-6">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-3xl font-bold">Product Management</h1>
        <Button onClick={saveProducts} disabled={saving}>
          {saving ? "Saving..." : "Save Changes"}
        </Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Product List */}
        <div className="lg:col-span-1">
          <Card>
            <CardHeader>
              <CardTitle>Products ({products.length})</CardTitle>
            </CardHeader>
            <CardContent className="space-y-2 max-h-[600px] overflow-y-auto">
              {products.map((product) => (
                <div
                  key={product.slug}
                  className={`p-3 rounded-lg border cursor-pointer transition-colors ${
                    selectedProduct?.slug === product.slug ? "bg-primary/10 border-primary" : "hover:bg-muted"
                  }`}
                  onClick={() => setSelectedProduct(product)}
                >
                  <div className="flex items-center space-x-3">
                    <img
                      src={product.image || "/placeholder.svg"}
                      alt={product.title}
                      className="w-12 h-12 object-cover rounded"
                    />
                    <div className="flex-1 min-w-0">
                      <p className="font-medium truncate">{product.title}</p>
                      <p className="text-sm text-muted-foreground">{product.type}</p>
                      <p className="text-sm font-medium">
                        ${product.priceMin}
                        {product.priceMax ? ` - $${product.priceMax}` : ""}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </CardContent>
          </Card>
        </div>

        {/* Product Editor */}
        <div className="lg:col-span-2">
          {selectedProduct ? (
            <Card>
              <CardHeader>
                <CardTitle>Edit Product: {selectedProduct.title}</CardTitle>
              </CardHeader>
              <CardContent>
                <Tabs defaultValue="basic" className="space-y-4">
                  <TabsList>
                    <TabsTrigger value="basic">Basic Info</TabsTrigger>
                    <TabsTrigger value="images">Images</TabsTrigger>
                    <TabsTrigger value="pricing">Pricing</TabsTrigger>
                  </TabsList>

                  <TabsContent value="basic" className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="title">Product Title</Label>
                        <Input
                          id="title"
                          value={selectedProduct.title}
                          onChange={(e) => handleProductUpdate("title", e.target.value)}
                        />
                      </div>
                      <div>
                        <Label htmlFor="type">Type</Label>
                        <Select
                          value={selectedProduct.type}
                          onValueChange={(value) => handleProductUpdate("type", value)}
                        >
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="Bypass">Bypass</SelectItem>
                            <SelectItem value="Bifold">Bifold</SelectItem>
                            <SelectItem value="Pivot">Pivot</SelectItem>
                            <SelectItem value="Barn">Barn</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </div>

                    <div>
                      <Label htmlFor="description">Description</Label>
                      <Textarea
                        id="description"
                        value={selectedProduct.description || ""}
                        onChange={(e) => handleProductUpdate("description", e.target.value)}
                        placeholder="Product description..."
                      />
                    </div>

                    <div>
                      <Label>Badges</Label>
                      <div className="flex flex-wrap gap-2 mt-2">
                        {selectedProduct.badges?.map((badge, index) => (
                          <Badge key={index} variant="secondary">
                            {badge}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  </TabsContent>

                  <TabsContent value="images" className="space-y-4">
                    <div>
                      <Label>Primary Image</Label>
                      <div className="mt-2 space-y-4">
                        <img
                          src={selectedProduct.image || "/placeholder.svg"}
                          alt={selectedProduct.title}
                          className="w-48 h-48 object-cover rounded-lg border"
                        />
                        <MediaSelector
                          onSelect={handleImageSelect}
                          maxSelections={1}
                          trigger={<Button variant="outline">Change Primary Image</Button>}
                        />
                      </div>
                    </div>

                    <div>
                      <Label>Gallery Images</Label>
                      <div className="mt-2 space-y-4">
                        {selectedProduct.gallery && selectedProduct.gallery.length > 0 && (
                          <div className="grid grid-cols-4 gap-2">
                            {selectedProduct.gallery.map((img, index) => (
                              <img
                                key={index}
                                src={img || "/placeholder.svg"}
                                alt={`Gallery ${index + 1}`}
                                className="w-24 h-24 object-cover rounded border"
                              />
                            ))}
                          </div>
                        )}
                        <MediaSelector
                          onSelect={handleGallerySelect}
                          maxSelections={10}
                          selectedFiles={selectedProduct.gallery || []}
                          trigger={
                            <Button variant="outline">
                              {selectedProduct.gallery?.length ? "Update Gallery" : "Add Gallery Images"}
                            </Button>
                          }
                        />
                      </div>
                    </div>
                  </TabsContent>

                  <TabsContent value="pricing" className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="priceMin">Minimum Price (CAD)</Label>
                        <Input
                          id="priceMin"
                          type="number"
                          value={selectedProduct.priceMin}
                          onChange={(e) => handleProductUpdate("priceMin", Number.parseInt(e.target.value))}
                        />
                      </div>
                      <div>
                        <Label htmlFor="priceMax">Maximum Price (CAD)</Label>
                        <Input
                          id="priceMax"
                          type="number"
                          value={selectedProduct.priceMax || ""}
                          onChange={(e) =>
                            handleProductUpdate(
                              "priceMax",
                              e.target.value ? Number.parseInt(e.target.value) : undefined,
                            )
                          }
                        />
                      </div>
                    </div>
                  </TabsContent>
                </Tabs>
              </CardContent>
            </Card>
          ) : (
            <Card>
              <CardContent className="flex items-center justify-center h-64">
                <p className="text-muted-foreground">Select a product to edit</p>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "../../components/ui/card"
import { Button } from "../../components/ui/button"
import { RefreshCw, File, ImageIcon, Video, FileText } from "lucide-react"

interface BlobFile {
  url: string
  pathname: string
  size: number
  uploadedAt: string
  filename: string
}

export default function BlobContentsPage() {
  const [files, setFiles] = useState<BlobFile[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  const fetchFiles = async () => {
    setLoading(true)
    setError(null)
    try {
      const response = await fetch("/api/list")
      if (!response.ok) throw new Error("Failed to fetch files")
      const data = await response.json()
      setFiles(data.files || [])
    } catch (err) {
      setError(err instanceof Error ? err.message : "Unknown error")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchFiles()
  }, [])

  const getFileIcon = (filename: string) => {
    const ext = filename.split(".").pop()?.toLowerCase()
    if (["jpg", "jpeg", "png", "gif", "webp"].includes(ext || "")) return <ImageIcon className="w-4 h-4" />
    if (["mp4", "mov", "avi", "webm"].includes(ext || "")) return <Video className="w-4 h-4" />
    if (["txt", "md", "json"].includes(ext || "")) return <FileText className="w-4 h-4" />
    return <File className="w-4 h-4" />
  }

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return "0 Bytes"
    const k = 1024
    const sizes = ["Bytes", "KB", "MB", "GB"]
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return Number.parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i]
  }

  return (
    <div className="container mx-auto p-6">
      <div className="flex items-center justify-between mb-6">
        <h1 className="text-3xl font-bold">Blob Storage Contents</h1>
        <Button onClick={fetchFiles} disabled={loading} className="flex items-center gap-2">
          <RefreshCw className={`w-4 h-4 ${loading ? "animate-spin" : ""}`} />
          Refresh
        </Button>
      </div>

      {error && (
        <Card className="mb-6 border-red-200 bg-red-50">
          <CardContent className="p-4">
            <p className="text-red-600">Error: {error}</p>
          </CardContent>
        </Card>
      )}

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <File className="w-5 h-5" />
            Files in Blob Storage ({files.length})
          </CardTitle>
        </CardHeader>
        <CardContent>
          {loading ? (
            <div className="flex items-center justify-center py-8">
              <RefreshCw className="w-6 h-6 animate-spin" />
              <span className="ml-2">Loading files...</span>
            </div>
          ) : files.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <File className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>No files found in Blob storage</p>
            </div>
          ) : (
            <div className="space-y-3">
              {files.map((file, index) => (
                <div key={index} className="flex items-center justify-between p-3 border rounded-lg hover:bg-gray-50">
                  <div className="flex items-center gap-3">
                    {getFileIcon(file.filename)}
                    <div>
                      <p className="font-medium">{file.filename}</p>
                      <p className="text-sm text-gray-500">
                        {formatFileSize(file.size)} • Uploaded {new Date(file.uploadedAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
