"use client"

import { useState, useEffect } from "react"
import { Button } from "../ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "../ui/card"
import { RefreshCw, File, ImageIcon, Video, FileText } from "lucide-react"

interface BlobFile {
  url: string
  pathname: string
  size: number
  uploadedAt: string
}

export function BlobStorageViewer() {
  const [files, setFiles] = useState<BlobFile[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  const fetchFiles = async () => {
    try {
      setLoading(true)
      setError(null)
      const response = await fetch("/api/list")

      if (!response.ok) {
        throw new Error(`Failed to fetch files: ${response.statusText}`)
      }

      const data = await response.json()
      setFiles(data.blobs || [])
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to fetch files")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchFiles()
  }, [])

  const getFileIcon = (pathname: string) => {
    const ext = pathname.split(".").pop()?.toLowerCase()
    if (["jpg", "jpeg", "png", "gif", "webp"].includes(ext || "")) return ImageIcon
    if (["mp4", "mov", "avi", "mkv"].includes(ext || "")) return Video
    if (["txt", "md", "json"].includes(ext || "")) return FileText
    return File
  }

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return "0 Bytes"
    const k = 1024
    const sizes = ["Bytes", "KB", "MB", "GB"]
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return Number.parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i]
  }

  return (
    <Card className="w-full max-w-4xl mx-auto">
      <CardHeader className="flex flex-row items-center justify-between">
        <CardTitle>Blob Storage Contents</CardTitle>
        <Button onClick={fetchFiles} disabled={loading} variant="outline" size="sm">
          <RefreshCw className={`h-4 w-4 mr-2 ${loading ? "animate-spin" : ""}`} />
          Refresh
        </Button>
      </CardHeader>
      <CardContent>
        {loading && (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div>
            <p className="mt-2 text-sm text-gray-600">Loading files...</p>
          </div>
        )}

        {error && (
          <div className="text-center py-8">
            <p className="text-red-600">Error: {error}</p>
            <Button onClick={fetchFiles} className="mt-2 bg-transparent" variant="outline">
              Try Again
            </Button>
          </div>
        )}

        {!loading && !error && (
          <>
            <div className="mb-4 text-sm text-gray-600">
              Found {files.length} file{files.length !== 1 ? "s" : ""} in storage
            </div>

            {files.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No files uploaded yet. Upload some files to see them here!
              </div>
            ) : (
              <div className="space-y-3">
                {files.map((file, index) => {
                  const IconComponent = getFileIcon(file.pathname)
                  const isImage = ["jpg", "jpeg", "png", "gif", "webp"].includes(
                    file.pathname.split(".").pop()?.toLowerCase() || "",
                  )

                  return (
                    <div key={index} className="flex items-center gap-4 p-3 border rounded-lg hover:bg-gray-50">
                      <div className="flex-shrink-0">
                        {isImage ? (
                          <img
                            src={file.url || "/placeholder.svg"}
                            alt={file.pathname}
                            className="w-12 h-12 object-cover rounded"
                          />
                        ) : (
                          <div className="w-12 h-12 bg-gray-100 rounded flex items-center justify-center">
                            <IconComponent className="h-6 w-6 text-gray-600" />
                          </div>
                        )}
                      </div>

                      <div className="flex-1 min-w-0">
                        <p className="font-medium truncate">{file.pathname}</p>
                        <p className="text-sm text-gray-500">
                          {formatFileSize(file.size)} • Uploaded {new Date(file.uploadedAt).toLocaleDateString()}
                        </p>
                      </div>

                      <Button variant="outline" size="sm" onClick={() => window.open(file.url, "_blank")}>
                        View
                      </Button>
                    </div>
                  )
                })}
              </div>
            )}
          </>
        )}
      </CardContent>
    </Card>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent } from "../ui/card"
import { Button } from "../ui/button"
import { Input } from "../ui/input"
import { Badge } from "../ui/badge"
import { useToast } from "@/hooks/use-toast"

interface BlobFile {
  url: string
  pathname: string
  size: number
  uploadedAt: string
  filename: string
}

interface MediaGalleryProps {
  selectable?: boolean
  onSelect?: (file: BlobFile) => void
  selectedFiles?: string[]
  maxSelections?: number
  fileTypes?: string[]
}

export function MediaGallery({
  selectable = false,
  onSelect,
  selectedFiles = [],
  maxSelections = 1,
  fileTypes = [],
}: MediaGalleryProps) {
  const [files, setFiles] = useState<BlobFile[]>([])
  const [filteredFiles, setFilteredFiles] = useState<BlobFile[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid")
  const [filterType, setFilterType] = useState<"all" | "images" | "documents">("all")
  const { toast } = useToast()

  const fetchFiles = async () => {
    try {
      const response = await fetch("/api/list")
      const data = await response.json()
      let fileList = data.files || []

      if (fileTypes.length > 0) {
        fileList = fileList.filter((file: BlobFile) =>
          fileTypes.some((type) => file.filename.toLowerCase().endsWith(type.toLowerCase())),
        )
      }

      setFiles(fileList)
      setFilteredFiles(fileList)
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to load files",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const handleDelete = async (url: string) => {
    try {
      const response = await fetch("/api/delete", {
        method: "DELETE",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url }),
      })

      if (response.ok) {
        toast({
          title: "Success",
          description: "File deleted successfully",
        })
        fetchFiles()
      } else {
        throw new Error("Delete failed")
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to delete file",
        variant: "destructive",
      })
    }
  }

  const isImage = (filename: string) => /\.(jpg|jpeg|png|gif|webp)$/i.test(filename)
  const isDocument = (filename: string) => /\.(pdf|doc|docx|txt)$/i.test(filename)

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return "0 Bytes"
    const k = 1024
    const sizes = ["Bytes", "KB", "MB", "GB"]
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return Number.parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + " " + sizes[i]
  }

  useEffect(() => {
    let filtered = files

    if (searchTerm) {
      filtered = filtered.filter((file) => file.filename.toLowerCase().includes(searchTerm.toLowerCase()))
    }

    if (filterType !== "all") {
      filtered = filtered.filter((file) => {
        if (filterType === "images") return isImage(file.filename)
        if (filterType === "documents") return isDocument(file.filename)
        return true
      })
    }

    setFilteredFiles(filtered)
  }, [files, searchTerm, filterType])

  useEffect(() => {
    fetchFiles()
  }, [])

  if (loading) {
    return <div className="text-center py-8">Loading media...</div>
  }

  return (
    <div className="space-y-4">
      <div className="flex flex-col sm:flex-row gap-4 items-center justify-between">
        <div className="flex items-center space-x-2 flex-1">
          <div className="relative flex-1 max-w-sm">
            <SearchIcon />
            <Input
              placeholder="Search files..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
          <Button
            variant="outline"
            size="sm"
            onClick={() =>
              setFilterType(filterType === "all" ? "images" : filterType === "images" ? "documents" : "all")
            }
          >
            <FilterIcon />
            {filterType === "all" ? "All" : filterType === "images" ? "Images" : "Documents"}
          </Button>
        </div>

        <div className="flex items-center space-x-2">
          <Badge variant="secondary">{filteredFiles.length} files</Badge>
          <Button variant="outline" size="sm" onClick={() => setViewMode(viewMode === "grid" ? "list" : "grid")}>
            {viewMode === "grid" ? <ListIcon /> : <GridIcon />}
          </Button>
        </div>
      </div>

      {filteredFiles.length === 0 ? (
        <div className="text-center py-8 text-muted-foreground">
          {searchTerm || filterType !== "all" ? "No files match your search criteria." : "No files uploaded yet."}
        </div>
      ) : viewMode === "grid" ? (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredFiles.map((file) => (
            <Card
              key={file.url}
              className={`cursor-pointer transition-all hover:shadow-md ${
                selectable && selectedFiles.includes(file.url) ? "ring-2 ring-primary" : ""
              }`}
              onClick={() => selectable && onSelect?.(file)}
            >
              <CardContent className="p-3">
                <div className="aspect-square mb-2 bg-muted rounded-lg overflow-hidden">
                  {isImage(file.filename) ? (
                    <img
                      src={file.url || "/placeholder.svg"}
                      alt={file.filename}
                      className="w-full h-full object-cover"
                    />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center text-muted-foreground">
                      <div className="text-center">
                        <div className="text-2xl mb-1">📄</div>
                        <div className="text-xs">{file.filename.split(".").pop()?.toUpperCase()}</div>
                      </div>
                    </div>
                  )}
                </div>
                <div className="space-y-1">
                  <p className="text-sm font-medium truncate" title={file.filename}>
                    {file.filename}
                  </p>
                  <p className="text-xs text-muted-foreground">{formatFileSize(file.size)}</p>
                </div>
                {!selectable && (
                  <div className="flex items-center space-x-1 mt-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation()
                        window.open(file.url, "_blank")
                      }}
                    >
                      <EyeIcon />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation()
                        const a = document.createElement("a")
                        a.href = file.url
                        a.download = file.filename
                        a.click()
                      }}
                    >
                      <DownloadIcon />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => {
                        e.stopPropagation()
                        handleDelete(file.url)
                      }}
                      className="text-red-600 hover:text-red-700"
                    >
                      <TrashIcon />
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <div className="space-y-2">
          {filteredFiles.map((file) => (
            <Card
              key={file.url}
              className={`cursor-pointer transition-all hover:shadow-sm ${
                selectable && selectedFiles.includes(file.url) ? "ring-2 ring-primary" : ""
              }`}
              onClick={() => selectable && onSelect?.(file)}
            >
              <CardContent className="p-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    {isImage(file.filename) && (
                      <img
                        src={file.url || "/placeholder.svg"}
                        alt={file.filename}
                        className="w-12 h-12 object-cover rounded"
                      />
                    )}
                    <div>
                      <p className="font-medium">{file.filename}</p>
                      <p className="text-sm text-muted-foreground">
                        {formatFileSize(file.size)} • {new Date(file.uploadedAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                  {!selectable && (
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation()
                          window.open(file.url, "_blank")
                        }}
                      >
                        <EyeIcon />
                        View
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation()
                          const a = document.createElement("a")
                          a.href = file.url
                          a.download = file.filename
                          a.click()
                        }}
                      >
                        <DownloadIcon />
                        Download
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation()
                          handleDelete(file.url)
                        }}
                        className="text-red-600 hover:text-red-700"
                      >
                        <TrashIcon />
                        Delete
                      </Button>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  )
}

